mapper.select:
select sch.PK_SCH, res.PK_SCHRES, res.PK_EMP, emp.NAME_EMP, emp.CODE_EMP, dept.NAME_DEPT, slot.NAME_DATESLOT, sch.CNT_TOTAL, sch.CNT_USED from SCH_SCH sch inner join SCH_RESOURCE res on res.PK_SCHRES = sch.PK_SCHRES inner join BD_OU_EMPLOYEE emp on emp.PK_EMP = res.PK_EMP inner join BD_OU_DEPT dept on dept.PK_DEPT = sch.PK_DEPT inner join BD_CODE_DATESLOT slot on slot.PK_DATESLOT = sch.PK_DATESLOT where to_char(DATE_WORK, 'yyyy-mm-dd') = to_char(sysdate, 'yyyy-mm-dd') and dept.CODE_DEPT in
#{deptCode}
and slot.NAME_DATESLOT = (case when to_number(to_char(sysdate, 'HH24')) < 12 then '上午' else '下午' end)

select emp.NAME_EMP, emp.CODE_EMP, emp.MOBILE, dept.CODE_DEPT, dept.NAME_DEPT from BD_OU_EMPLOYEE emp left join BD_OU_EMPJOB job on job.PK_EMP = emp.PK_EMP left join BD_OU_DEPT dept on dept.PK_DEPT = job.PK_DEPT where emp.DEL_FLAG = '0'
AND emp.CODE_EMP = #{codeEmp}
AND emp.NAME_EMP LIKE '%' || #{name} || '%'
AND emp.MOBILE = #{phone}

SELECT CODE_DEPT, NAME_DEPT FROM BD_OU_DEPT dept WHERE DEL_FLAG = '0'
AND NAME_DEPT LIKE '%' || #{nameDept} || '%'
AND CODE_DEPT = #{codeDept}
